examples.basic:
def binary_search(arr, target):
    l, r = 0, len(arr) - 1
    while l <= r:
        mid = (l + r) // 2
        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            l = mid + 1
        else:
            r = mid - 1
    return -1

examples.advanced:
# region recursive version
def bs_rec(arr, target, l, r):
    if l > r: return -1
    mid = (l + r) // 2
    if arr[mid] == target: return mid
    if arr[mid] < target: return bs_rec(arr, target, mid+1, r)
    return bs_rec(arr, target, l, mid-1)
# endregion

examples.basic:
function binarySearch(arr, target) {
  let l = 0, r = arr.length - 1;
  while (l <= r) {
    const mid = Math.floor((l + r) / 2);
    if (arr[mid] === target) return mid;
    if (arr[mid] < target) l = mid + 1;
    else r = mid - 1;
  }
  return -1;
}

examples.advanced:
// region recursive version
function bsRec(arr, t, l, r) {
  if (l > r) return -1;
  const mid = Math.floor((l + r) / 2);
  if (arr[mid] === t) return mid;
  if (arr[mid] < t) return bsRec(arr, t, mid+1, r);
  return bsRec(arr, t, l, mid-1);
}
// endregion

