func BinarySearch(arr []int, target int) int {
    l, r := 0, len(arr)-1
    for l <= r {
        mid := (l + r) / 2
        if arr[mid] == target { return mid }
        if arr[mid] < target { l = mid + 1 } else { r = mid - 1 }
    }
    return -1
}

examples.advanced:
// region recursive version
func bsRec(arr []int, t, l, r int) int {
    if l > r { return -1 }
    mid := (l + r) / 2
    if arr[mid] == t { return mid }
    if arr[mid] < t { return bsRec(arr, t, mid+1, r) }
    return bsRec(arr, t, l, mid-1)
}
// endregion

examples.basic:
int binary_search(const vector<int>& arr, int target) {
    int l = 0, r = arr.size()-1;
    while (l <= r) {
        int mid = (l + r) / 2;
        if (arr[mid] == target) return mid;
        if (arr[mid] < target) l = mid + 1;
        else r = mid - 1;
    }
    return -1;
}

examples.advanced:
// region recursive version
int bsRec(const vector<int>& arr, int t, int l, int r) {
    if (l > r) return -1;
    int mid = (l + r) / 2;
    if (arr[mid] == t) return mid;
    if (arr[mid] < t) return bsRec(arr, t, mid+1, r);
    return bsRec(arr, t, l, mid-1);
}
// endregion
