examples.basic:
x = 10
if x > 5:
    print('big')
else:
    print('small')

examples.advanced:
# region chained conditions
x = 42
if x < 0:
    print('negative')
elif x == 0:
    print('zero')
else:
    print('positive')
# endregion

examples.basic:
let x = 10;
if (x > 5) {
  console.log('big');
} else {
  console.log('small');
}

examples.advanced:
// region nested conditionals
if (user) {
  if (user.active) {
    startSession();
  } else {
    logout();
  }
}
// endregion

examples.basic:
x := 10
if x > 5 {
    fmt.Println("big")
} else {
    fmt.Println("small")
}

examples.advanced:
// region init statement in if
if val := compute(); val > 100 {
    fmt.Println("large")
} else {
    fmt.Println("normal")
}
// endregion

examples.basic:
int x = 10;
if (x > 5) {
    std::cout << "big";
} else {
    std::cout << "small";
}

examples.advanced:
// region multiple conditions
int x = 42;
if (x < 0) std::cout << "neg";
else if (x == 0) std::cout << "zero";
else std::cout << "pos";
// endregion
