examples.basic:
for i in range(3):
    print(i)

x = 0
while x < 3:
    print(x)
    x += 1

examples.advanced:
# region nested loops
for i in range(2):
    for j in range(2):
        print(i, j)
# endregion

examples.basic:
for (let i = 0; i < 3; i++) {
  console.log(i);
}

let x = 0;
while (x < 3) {
  console.log(x);
  x++;
}

examples.advanced:
// region for...of and for...in
for (const v of arr) {
  console.log(v);
}
for (const key in obj) {
  console.log(key);
}
// endregion

examples.basic:
for i := 0; i < 3; i++ {
    fmt.Println(i)
}

x := 0
for x < 3 {
    fmt.Println(x)
    x++
}

examples.advanced:
// region range loop
nums := []int{1,2,3}
for _, v := range nums {
    fmt.Println(v)
}
// endregion

examples.basic:
for (int i = 0; i < 3; i++) {
    std::cout << i;
}

int x = 0;
while (x < 3) {
    std::cout << x;
    x++;
}

examples.advanced:
// region range-based loop
std::vector<int> nums = {1,2,3};
for (int v : nums) {
    std::cout << v;
}
// endregion
