# region operator overloading
class Vector:
    def __init__(self,x): self.x=x
    def __add__(self,o): return Vector(self.x + o.x)
# endregion

// region logical chaining
if (user && user.active && user.age > 18) {}
// endregion

// region pointer ops
x := 10
p := &x
*p = 20
// endregion

// region operator overloading
struct V{int x;};
V operator+(const V&a,const V&b){return V{a.x+b.x};}
// endregion
