// region queue struct
type Queue struct { data []int }
func (q *Queue) Enqueue(x int) { q.data = append(q.data, x) }
func (q *Queue) Dequeue() int {
    v := q.data[0]
    q.data = q.data[1:]
    return v
}
// endregion

