examples.basic:
let x = 5; // global in module
function f() {
  let y = 10; // local
  return y;
}

examples.advanced:
// region closure scope
function outer() {
  let x = 5;
  return function inner() { return x; };
}
// endregion

examples.basic:
var x int = 5 // package scope
func f() {
    y := 10 // local
    _ = y
}

examples.advanced:
// region closure scope
func outer() func() int {
    x := 5
    return func() int { return x }
}
// endregion

examples.basic:
int x = 5; // global
void f() {
    int y = 10; // local
}

examples.advanced:
// region lambda capture scope
auto outer = [](){
    int x = 5;
    return [x](){ return x; };
};
// endregion
