examples.advanced:
# region class-based stack
class Stack:
    def __init__(self): self.s = []
    def push(self, x): self.s.append(x)
    def pop(self): return self.s.pop()
# endregion

examples.basic:
const stack = [];
stack.push(1);
stack.push(2);
const val = stack.pop();

examples.advanced:
// region class-based stack
class Stack {
  constructor() { this.s = []; }
  push(x) { this.s.push(x); }
  pop() { return this.s.pop(); }
}
// endregion

// region struct stack
type Stack struct { data []int }
func (s *Stack) Push(x int) { s.data = append(s.data, x) }
func (s *Stack) Pop() int {
    v := s.data[len(s.data)-1]
    s.data = s.data[:len(s.data)-1]
    return v
}
// endregion

examples.basic:
std::vector<int> stack;
stack.push_back(1);
stack.push_back(2);
int val = stack.back();
stack.pop_back();

examples.advanced:
// region template stack
template<typename T>
class Stack {
  std::vector<T> s;
public:
  void push(T x){ s.push_back(x); }
  T pop(){ T v=s.back(); s.pop_back(); return v; }
};
// endregion

